function isAllTableAvailable() {

    var configTales = kaholo.execution.configuration.cloneTableNames;
    if (kaholo.execution.configuration.serverType == "MySql") {
        var resTables = kaholo.actions.QueryDB.result;
        if (JSON.stringify(resTables) != "[]") {
            if (resTables.length > 0) {
                var alltableFound = true;
                for (var i = 0; i < configTales.length; i++) {
                    const isFound = resTables.some(element => {
                        if (element.table === configTales[i]) {
                            return true;
                        } else {
                            return false;
                        }
                    });
                    if (!isFound) {
                        alltableFound = false;
                    }
                }
                if (alltableFound) {
                    return true;
                } else {
                    return false;
                }
